/* 各チケットのグラブ要素を非表示 */
div[class="ghx-grabber"]{
  display: none !important;
}

/* 各チケットの隙間を狭くする */
div.ghx-issue{
  padding: 4px 4px 4px 6px !important;
}

/* 各チケットのLabel等が持っているマージンを調整を狭くする */
div.ghx-issue>section{
  margin-top: 0px !important;
}
/* 各チケットのAssine等が持っているマージンを調整を狭くする */
div.ghx-row{
  margin-top: 0px !important;
}

/* ボードの上部のスペースを狭くする */
div[data-testid*="rapidboard-breadcrumbs"]{
  margin-top: 2px !important;
  margin-left: 15px !important;
}
/* ボード本体のスペースを狭くする */
div[class*="z-index-content"]{
  margin-left: 15px !important;
}
div[id="ghx-pool-column"]{
  padding-right: 16px !important;
}

/* ボード本体のカラム名部分のスペースを狭くする */
li[class*="ghx-column"]{
  padding-top: 0px !important;
  padding-bottom: 6px !important;
}
/* ボード本体のカラム名部分のスペースを狭くするのに伴い、カードが隙間から除くので、それを補正←もう不要。 */
div[class*="ghx-swimlane-header"]{
  top:24px !important;
}
/* ボード */
