/* フィルターによる検索結果 */

/* 行間に罫線を引く */
/*table[id=issuetable] tr.issuerow{
  border:solid 1px #eeeeee !important;
}*/

/* 親チケット列のhiddenを解除 */
table[id=issuetable] td.parent>p{
  white-space: normal !important;
}

/* 各セルの設定 */
table[id=issuetable] td{
  word-break: break-all !important; /* 各列の改行位置を「どこでも」へ変更する */
  border:solid 1px #eeeeee !important; /* 全セルに罫線を引く */
  padding: 4px 6px !important; /* 隙間を縮める。 */
}

/* スプリント列の幅を小さくして、はみ出た分は非表示にする */
table[id=issuetable] td.customfield_10006{
  text-overflow: ellipsis !important;
  max-width: 150px !important;
  white-space: nowrap !important;
  overflow: hidden !important;
}

/* 担当者列の幅を小さくして、はみ出た分は非表示にする */
table[id=issuetable] td.assignee{
  text-overflow: ellipsis !important;
  max-width: 100px !important;
  white-space: nowrap !important;
  overflow: hidden !important;
}
